import{_ as e,o as t,c as a,Q as o}from"./chunks/framework.725e5b03.js";const _=JSON.parse('{"title":"Introduction","description":"","frontmatter":{"title":"Introduction","outline":"deep"},"headers":[],"relativePath":"docs/guide/introduction.md","filePath":"docs/guide/introduction.md","lastUpdated":1702042266000}'),r={name:"docs/guide/introduction.md"},i=o('<h1 id="what-is-the-oxc-project" tabindex="-1">What is the Oxc Project? <a class="header-anchor" href="#what-is-the-oxc-project" aria-label="Permalink to &quot;What is the Oxc Project?&quot;">​</a></h1><p>The JavaScript Oxidation Compiler Project is a collection of high-performance tools for the JavaScript language written in Rust.</p><p>We are building JavaScript compiler tools such as parser, linter, formatter, transpiler, minifier and resolver.</p><p>We are also supporting new JavaScript tools such as Rspack, Rolldown and Ezno.</p><p>Our goal is to create and empower tooling for <a href="https://www.swyx.io/js-third-age" target="_blank" rel="noreferrer">The Third Age of JavaScript</a>.</p><h2 id="philosophy" tabindex="-1">Philosophy <a class="header-anchor" href="#philosophy" aria-label="Permalink to &quot;Philosophy&quot;">​</a></h2><p>This project shares the same philosophies as <a href="https://biomejs.dev" target="_blank" rel="noreferrer">Biome</a> and <a href="https://beta.ruff.rs" target="_blank" rel="noreferrer">Ruff</a>.</p><ol><li>JavaScript tooling could be rewritten in a more performant language.</li><li>An integrated toolchain can tap into efficiencies that are not available to a disparate set of tools.</li></ol>',8),n=[i];function s(c,l,p,h,d,f){return t(),a("div",null,n)}const m=e(r,[["render",s]]);export{_ as __pageData,m as default};
